INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002657, N'سخانات الغمر', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كعنصر تسخين مغمور في الماء ليتم تسخينه. يمكن بيع هذه المنتجات كوحدات منفصلة أو يمكن دمجها في التصميم العام للغلاية. يشمل منتجات مثل السخانات الغاطسة ذات القابس اللولبي ، والسخانات ذات الحواف ، والسخانات الجانبية ، حيث يلزم سهولة الوصول للتنظيف.', N'يستثنى من ذلك منتجات مثل الغلايات الكاملة.')
