INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005717, N'دفايات الغرفة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أجهزة تدفئة للغرفة يتم تشغيلها إما بالكهرباء أو الغاز أو الفحم أو الخشب. يشمل منتجات مثل سخانات الفضاء القائمة بذاتها والمواقد بالإضافة إلى اللوح الأساسي وسخانات الحائط.', N'يستثنى من ذلك منتجات مثل السخانات الغاطسة والمشعات والسخانات الأرضية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005875, N'محطات الطاقة الشمسية', N'يتضمن أي منتجات يمكن وصفها / استيعابه كمجموعة سهلة التركيب تتكون من لوحة شمسية عالية الطاقة وبطارية ووحدة تحكم في الشحن وعاكس. يمكن لمحطة الطاقة تشغيل معظم الأجهزة الموجودة في المنزل والمكتب ومكان العمل - مثل أجهزة التلفزيون وأجهزة الاستريو ووحدات التحكم في الألعاب وأجهزة الكمبيوتر المحمول والفاكس والأدوات الكهربائية.', N'لا يشمل جميع السخانات التي تعمل بالغاز والكهرباء.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005479, N'سخانات مياه بدون خزان', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز يتم تركيبه على أنبوب مياه قريب من مكان استخدام المياه والذي يسخن الماء أثناء تدفق المياه عبر الجهاز. يتم تسخين المياه على الفور ، بناءً على طلب المستخدم ، مما يوفر المياه ولأنه ليس من الضروري تخزين المياه ، يتم الحفاظ على الطاقة أيضًا. يشمل المنتجات التي تعمل بالكهرباء أو الغاز.', N'يستثنى من ذلك منتجات مثل السخانات الغاطسة وسخانات مياه الخزان.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10004002, N'ثرموستات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز ينظم درجة الحرارة تلقائيًا في غرفة أو مبنى عن طريق قطع واستعادة إمداد الحرارة من نظام التدفئة المركزية. تشمل منظمات الحرارة الميكانيكية وأجهزة تنظيم الحرارة الرقمية التي يمكن التحكم فيها عن بُعد بواسطة نظام التشغيل الآلي للمنزل.', N'باستثناء منتجات مثل منظمات الحرارة غير المخصصة للتحكم في نظام التدفئة المركزية للمبنى.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10004003, N'أنظمة التدفئة تحت الأرضية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها شبكة من الأنابيب أو كابلات التدفئة الموضوعة في أرضية المبنى أو المنزل. تتولد الحرارة إما عن طريق الماء الساخن المتدفق عبر الأنابيب أو الكابلات المسخنة كهربائياً ، مما يؤدي إلى تدفئة الغرفة أو المبنى بشكل فعال.', N'يستثنى من ذلك منتجات مثل المشعات والثرموستات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10008060, N'ملحقات مواسير الهواء وقطع الغيار', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجزء أو ملحق لمجاري الهواء واستخراج الدخان. تشمل منتجات مثل الأقواس / الحلقات وألواح / أغلفة المداخن (وريدات) لتطبيقات استخراج الدخان.', N'يستثني موصلات مجاري الهواء. باستثناء المدخنة وأغطية المطر. يستثنى على وجه التحديد مجاري الهواء / موصلات مجرى الهواء.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10008166, N'زجاج (مُشكَّل)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها زجاج خام لم تتم معالجتها بعد.', N'استبعد أي مواد خام غير مشوهة أو تمت معالجتها.')
