INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002590, N'وحدات حوض الاستحمام / الدش', N' يشمل أي منتجات يمكن وصفها / ملاحظتها كتركيب مستقل من قطعة واحدة يتكون من حوض استحمام ومرافق دش.', N'يستثنى على وجه التحديد أحواض الاستحمام / وحدات الدش المزودة بنفاثات مائية مدمجة. يُستثنى من ذلك المنتجات مثل أحواض الاستحمام والاستحمام التي تُباع بشكل فردي والمنتجعات الصحية.')
