INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10004029, N'وحدات حوض الاستحمام / الدش - متدفقة', N' يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها تركيب مستقل من قطعة واحدة يتكون من حوض استحمام ومرافق دش. هذه المنتجات مزودة بفتحات صغيرة تنبعث منها تيارات المياه بالطاقة لإحداث تأثير تدليك على الجسم.', N'يستثنى على وجه التحديد أحواض الاستحمام / وحدات الدش التي لا تحتوي على نوافير مياه مدمجة. يُستثنى من ذلك المنتجات مثل أحواض الاستحمام والاستحمام التي تُباع بشكل فردي والمنتجعات الصحية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002596, N'أحواض الاستحمام - متدفقة (أحواض المياه الساخنة / المنتجعات الصحية)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها حاوية كبيرة مفتوحة تُستخدم للاستحمام والتدليك. هذه المنتجات مزودة بفتحات صغيرة تنبعث منها تيارات المياه بالطاقة لإحداث تأثير تدليك على الجسم. عادةً ما تحتوي هذه المنتجات على صنبور / حنفية واحدة أو أكثر يتم إرفاقها لملئها بالماء ، وثقب في القاع يتم من خلاله طرد المياه المستعملة أو الرمادية بمجرد عدم الحاجة إليها. هذه الحمامات والمنتجعات الصحية كبيرة بما يكفي بحيث يمكن لشخص بالغ أو أكثر أن يدخلها ويغمر نفسه في الماء. يشمل منتجات مثل أحواض المياه الساخنة في الهواء الطلق.', N'يستثنى على وجه التحديد أحواض الاستحمام التي لا تحتوي على نوافير مائية متكاملة. باستثناء المنتجات مثل الأحواض / الأحواض ووحدات حوض الاستحمام / الدش.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002595, N'أحواض الاستحمام - نقع', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها حاوية كبيرة مفتوحة تُستخدم للاستحمام. عادةً ما تحتوي هذه المنتجات على صنبور / حنفية واحدة أو أكثر يتم إرفاقها لملئها بالماء ، وثقب في القاع يتم من خلاله طرد المياه المستعملة أو الرمادية بمجرد عدم الحاجة إليها. هذه الأحواض كبيرة بما يكفي بحيث يمكن لشخص بالغ أن يدخلها ويغمر نفسه في ماء الاستحمام. يشمل منتجات مثل الحمامات القائمة بذاتها والحمامات الغارقة.', N'يستثنى على وجه التحديد أحواض الاستحمام ذات النفاثات المائية المتكاملة وأحواض الاستحمام الساخنة. باستثناء المنتجات مثل الأحواض / الأحواض ووحدات حوض الاستحمام / الدش.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007941, N'قطع غيار / ملحقات الحنفية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جزء / ملحق بديل للصنابير. تشمل منتجات مثل أجهزة التهوية اللولبية وأدوات التحكم في التدفق وغسالات وفوهات الحنفيات / الحنفية.', N'باستثناء قطع الغيار / الملحقات للأدوات الصحية الأخرى.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002602, N'الحنفيات / الصنابير', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها تركيبات سباكة تستخدم لتوجيه والتحكم في تدفق الماء البارد / الساخن. يتم توصيلها عادةً بالأحواض / الأحواض أو أحواض الاستحمام بحيث يمكن ملؤها بالماء بسرعة وسهولة. قد يتم تشغيل هذه المنتجات أو تشغيلها يدويًا وقد تتضمن تركيبات أخرى تعمل بالطاقة ، مثل أجهزة الاستشعار لاكتشاف اليدين من أجل بدء تدفق المياه تلقائيًا. يشمل منتجات مثل الحنفية ذات درجة الحرارة الواحدة / الحنفيات والخلاطات (صنبور / صنبور واحد يتدفق من خلاله كل من الماء البارد والساخن)', N'باستثناء المنتجات مثل فوهات الحنفية / الحنفية والصهاريج ورؤوس الدش.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007723, N'لوحة الحائط المتدفقة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها صفيحة تستخدم لغسل محتويات صهريج المرحاض في أنبوب نفايات. يتم تنشيط آلية التدفق داخل الخزان عندما يتم سحب أو دفع مقبض أو زر متصل بالخارج. يمكن أيضًا تنشيط آلية التنظيف تلقائيًا عن طريق المستشعرات التي تزيل الحاجة إلى مقبض أو زر. يتضمن لوحات دافق مثبتة على الحائط.', N'يستثنى على وجه التحديد ألواح التدفق أو آليات التدفق المتصلة بالمرحاض أو صهريج المبولة.')
GO
