INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002612, N'الأدوات الصحية - قطع غيار / ملحقات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جزء بديل أو ملحق لمنتجات الأدوات الصحية. يتضمن منتجات مثل مجموعات حامل حوض الاستحمام ، وقضبان لتثبيت جدران الدش ومساند رأس الحمام.', N'باستثناء منتجات مثل مقاعد الحمام وشرائط أمان الدش ومقابض المرحاض البديلة والصمامات والعوامات وأجزاء الحنفية / الحنفية وقضبان المناشف.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002614, N'عبوات متنوعة للأدوات الصحية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها منتجين أو أكثر من منتجات الأدوات الصحية المميزة التي تباع معًا ، والتي توجد داخل المخطط والتي تنتمي إلى طوب مختلف ولكن لنفس الفئة ، أي منتجين أو أكثر متضمنين في نفس العبوة والتي تتقاطع مع الطوب بداخلها فئة الأدوات الصحية. يشمل منتجات مثل أبواب الدش المنفصلة التي تُباع بصواني دش ومراحيض ومباول منفصلة تُباع معًا. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'باستثناء منتجات مثل المراحيض ومقاعد المراحيض المتنوعة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007974, N'ستائر الحمام - الإكسسوارات وقطع الغيار', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها إكسسوار أو جزء بديل لستائر الدوش. يتضمن على وجه التحديد حلقات لإرفاق الستارة بالشريط أو الشريط نفسه.', N'يستثني أي نوع آخر من قطع غيار أو ملحقات الأدوات الصحية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002597, N'مرفقات / أجنحة الاستحمام', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها حاوية كاملة للاستحمام. سيتألف الجناح ، الذي يباع كوحدة واحدة ، من حوض استحمام / صينية وألواح دش محيطية وباب دش. قد يشتمل الجناح على رؤوس دُش ونفاثات للتدليك المائي وقد لا يشملها.', N'باستثناء المنتجات مثل صواني الدش والألواح والأبواب التي تُباع بشكل فردي وحزم متنوعة للأدوات الصحية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002599, N'صواني / أحواض الاستحمام', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها قاعدة لوحدة الاستحمام ، والتي تكون غارقة في مستوى أدنى من الأبواب. تهدف هذه المنتجات إلى احتواء الماء من رأس الدش ، والذي يتم بعد ذلك تصريفه بعيدًا من خلال فخ الدش. يشمل منتجات بأحجام وأشكال متنوعة.', N'باستثناء المنتجات مثل مرفقات الاستحمام.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007726, N'مجموعات الاستحمام', N'تشمل أي منتجات يمكن وصفها / ملاحظتها كمجموعة مصنوعة من مجموعة من المنتجات التالية: شريط منزلق الدش ، رأس دش ، ترموستات ، إلخ. يمكن أيضًا تسمية هذه المجموعات بحزم الكل في واحد.', N'لا يشمل كبائن الاستحمام الكاملة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10004044, N'الحمامات والمنتجعات الصحية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها نظام من نفاثات الدوامة داخل حاوية الدش ، حيث يتم إغلاق الصرف أثناء تشغيل المضخة وإعادة تدوير المياه ، مما يؤدي إلى إحداث تأثير تدليك لشخص يقف في الحمام. تتيح لوحة اللمس الإلكترونية للمستخدم ضبط تدفق المياه لإجراء التدليك المطلوب ، كما يساعد السخان المتكامل في الحفاظ على درجة حرارة الماء الأصلية.', N'باستثناء المنتجات مثل أحواض الاستحمام المتدفقة ووحدات حوض الاستحمام والدش المدمجة حيث تم تركيب نفاثات مائية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002593, N'مجموعات الحوض / القاعدة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها حوض / حوض مائي ووحدة أساسية معًا. يمكن أن تكون هذه المنتجات قائمة بذاتها أو مثبتة على الحائط أو على الأرض.', N'يُستثنى من ذلك المنتجات مثل الأحواض / الأحواض والركائز التي تُباع بشكل فردي.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006961, N'اكسسوارات المرحاض', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها ملحق مصمم خصيصًا للاستخدام بالقرب من المرحاض. يتضمن منتجات مثل حاملات ورق التواليت ، وفرش المرحاض ، وحامل فرشاة المرحاض.', N'يستثنى من ذلك منتجات مثل مقاعد المراحيض وأغطية المراحيض.')
