INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007016, N'أطقم المرحاض / بيديت', N'تتضمن أي منتجات يمكن وصفها / ملاحظتها على أنها مجموعة مرحاض / بيديه كاملة بما في ذلك الإطار والمرحاض / بيديه نفسها.', N'يُستثنى من ذلك إطارات المراحيض / المراحيض والأغطية التي تُباع بشكل منفصل.')
