INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007018, N'أغطية المرحاض / بيديه للحائط', N'يتضمن أي منتجات يمكن وصفها / ملاحظتها على أنها لوحة يتم وضعها على الحائط بين الجدار وهيكل المرحاض / بيديه ، وقد تشتمل على زر ضغط لتشغيل الشطف.', N'باستثناء إطارات المرحاض / الشطاف والأغطية الأخرى للأدوات الصحية المصنفة بالفعل.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007017, N'مرحاض / بيديت / مبولات / قطع غيار / ملحقات', N' يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جزء / ملحق بديل للمراحيض / المراحيض / المبولات. تشمل منتجات مثل مواد التثبيت والموصلات والمفصلات للمراحيض أو مقاعد المراحيض وآليات الشطف والأسلاك والأزرار والمقابض.', N'باستثناء قطع الغيار / الملحقات للأدوات الصحية ومقاعد المرحاض والإطارات الأخرى.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002589, N'مراحيض / صهاريج مبولة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها خزان يحتوي على كمية محددة من المياه المستخدمة لغسل محتويات وعاء المرحاض في أنبوب نفايات. يتم تنشيط آلية التدفق داخل الخزان عندما يتم سحب أو دفع مقبض أو زر متصل بالخارج. يمكن أيضًا تنشيط آلية التنظيف تلقائيًا عن طريق المستشعرات التي تزيل الحاجة إلى مقبض أو زر. تشمل منتجات مثل الصهاريج العالية والمنخفضة والمخفية والمغلقة.', N'يستثنى من ذلك المنتجات مثل المبولات والمراحيض والأحواض / الأحواض.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002586, N'مراحيض', N' يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها تركيبات سباكة مصممة خصيصًا للتخلص من النفايات الجسدية مثل البول والبراز. عادةً ما يتم توصيل هذه المنتجات بإمدادات مياه جارية ونظام تصريف ، ويجلس المستخدم عليها أو يجلس فوقها. يشمل منتجات مثل مراحيض التنظيف ، ومراحيض الكومود ومراحيض القرفصاء.', N'يستثنى على وجه التحديد المبولات ومراحيض التخييم. يستثنى من ذلك منتجات مثل صهاريج المرحاض وماكراتور وأجنحة الحمام.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002588, N'أقسام مبولة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كشاشة أو لوحة مثبتة على الحائط مصممة لتوضع بين المبولات لتوفير الخصوصية عند التبول.', N'يستثنى من ذلك منتجات مثل الأقسام غير المخصصة لاستخدام الحمام وألواح الحائط الخاصة بدش وحوض الاستحمام.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002587, N'المبولات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها تركيبات السباكة المصممة خصيصًا للتبول ، وعادةً من قبل رجل واقف. قد تكون هذه المنتجات مصممة للاستخدام من قبل شخص واحد أو أكثر ، وعادة ما تكون عبارة عن وعاء مثبت على الحائط. قد يكون لديهم أو لا يكون لديهم نظام شطف لشطف البول من الوعاء.', N'يستثنى من ذلك المنتجات مثل المراحيض ، والبيديه ، وأجهزة التقطيع والمراحيض / صهاريج المبولة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002611, N'Macerators', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز مثبت في خط تصريف من المرحاض أو حوض المطبخ ، والذي يستخدم مضخة لطحن النفايات وإرسالها بأمان إلى نظام الصرف الصحي.', N'باستثناء المنتجات مثل خزانات الصرف الصحي.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10004049, N'خزانات الصرف الصحي', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها خزان مغلق للاستقبال والتخزين المؤقت لمياه الصرف الصحي حيث يكون الوصول إلى شبكة الصرف الصحي محدودًا للغاية. عادة ما يخضع التثبيت لأنظمة البناء. تشمل المنتجات التي تلبي احتياجات المساكن الفردية لما يصل إلى ستة أشخاص بالإضافة إلى المنتجات التي تلبي احتياجات المساكن التي يصل عدد أفرادها إلى خمسين شخصًا.', N'يستثنى من ذلك المنتجات مثل أجهزة التقطيع ومصائد النفايات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005480, N'مصائد / مصارف النفايات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جهاز يمنع دخول غازات المجاري والروائح الكريهة إلى المنزل ، أو يمنع دخول النفايات مثل الشحوم ومواد المطبخ الصلبة إلى شبكة الصرف الصحي. تشمل منتجات مثل مصائد الأحواض ومصائد الشحوم ومصائد الاستحمام ومخلفات السباكة.', N'باستثناء المنتجات مثل أنابيب السباكة وخزانات الصرف الصحي.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10004006, N'مزيلات القشور (DIY)', N' يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مستحضر عالي القوة / مركز ، مصمم خصيصًا للاستخدام الذاتي ، لإزالة رواسب الجير الناتجة عن الماء العسر. عادةً ما تُستخدم هذه المنتجات في إزالة حجم الأنابيب وأنظمة تخزين / تسخين المياه.', N'يستثنى على وجه التحديد المنتجات المصممة للمناطق والأجهزة المنزلية العامة (مثل الغلايات ومكاوي البخار والحمامات ونفاثات الدش والصنابير). يستثنى من ذلك منتجات مثل معقمات المياه وفلاتر المياه والمنظفات المنزلية ومجموعات معالجة المياه.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002649, N'مثبطات التقشر', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز متصل بأنابيب إمداد بالمياه لمنع / منع التقشر على وجه التحديد ، إما بشكل مؤقت أو دائم. من خلال تغيير هيكل أو خصائص جزيئات الميزان ، تمنع هذه الأجهزة من الالتصاق أو الترسب على تركيبات السباكة أو أعمال الأنابيب أو المبادلات الحرارية. يتم دمج هذه المنتجات كجزء من نظام الأنابيب أو يتم توصيلها خارجيًا. تشمل منتجات مثل مثبطات المقياس المغناطيسي ومثبطات الترسبات الكلسية.', N'يستثني على وجه التحديد مزيلات القشور التي تزيل الترسبات بمجرد إيداعها. يستثنى من ذلك منتجات مثل فلاتر المياه وعسر الماء ومعالجة المياه.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10004016, N'آلات / أنظمة تنقية المياه', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كآلة / نظام مثبت في مكانه لتصفية المياه من مصدر مياه متصل. يتم تمرير الماء من خلال مرشح واحد أو أكثر داخل الجهاز / النظام لإزالة الشوائب مثل المعادن ورواسب المياه والكلور ومبيدات الأعشاب ومبيدات الآفات. تشمل المنتجات التي تستخدم فلاتر الأشعة فوق البنفسجية (UV) والتناضح العكسي.', N'يستثنى على وجه التحديد أباريق / أوعية ترشيح المياه المحمولة. باستثناء المنتجات مثل خراطيش تصفية المياه ، وعسر الماء ، وأطقم معالجة المياه ، ومستلزمات ترشيح المياه ، ومزيلات القشور ومثبطات التقشر.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10004012, N'عدادات المياه', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز ، والتي تقيس حجم المياه التي تأخذها الأسرة من مصدر رئيسي لاستخدامها في المنزل. يشمل المنتجات ذات العرض الرقمي لإظهار حجم المياه المستخدمة.', N'يستثنى من ذلك منتجات مثل خزانات تخزين المياه.')
