INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005396, N'أجهزة الإنذار ضد السرقة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كنظام إلكتروني لأجهزة الاستشعار المتصلة بوحدة تصدر صوتًا تحذيريًا أو إشارة فوق صوتية عند تنشيطها بواسطة شكل من أشكال التطفل. تشمل منتجات مثل صانعات التشويش اللاسلكية المستقلة وأجهزة الإنذار بنقاط التوقف والأنظمة الرقمية متعددة المناطق.', N'يستثنى من ذلك منتجات مثل أجهزة إنذار الحريق العامة وأجهزة كشف الغاز والدخان وكاشفات كسر الزجاج وأجهزة الإنذار الشخصية.')
