INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005397, N'كاشفات الغاز / الحرارة / الدخان', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مستشعر إلكتروني يكتشف تسرب الغاز أو انبعاثات الحرارة أو الدخان ويرسل تنبيهًا بتنبيه مرئي أو مسموع أو اهتزازي. تشمل منتجات مثل أجهزة الإنذار التي تعمل بالبطاريات المستقلة وتلك التي تعمل بالكهرباء.', N'يستثنى من ذلك منتجات مثل أجهزة إنذار الحريق العامة وأجهزة الكشف عن كسر الزجاج وأجهزة الإنذار ضد السرقة وأجهزة الإنذار الشخصية.')
