INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007008, N'كاشف كسر الزجاج', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جهاز استشعار يكتشف ما إذا كان الزجاج مكسورًا / متكسرًا. يمكن أيضًا استخدام هذه الأجهزة لأغراض أخرى غير منع السطو. تُستخدم أجهزة الاستشعار هذه بشكل شائع بالقرب من الأبواب الزجاجية أو النوافذ الأمامية الزجاجية.', N'باستثناء المنتجات مثل أجهزة الإنذار ضد السرقة وأجهزة الإنذار الشخصية.')
