INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007007, N'منتج أمان مضاد للتسلق / رادع', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها شريط من المسامير أو المغازل أو الياقات التي تثبط التسلق. يمكن تأمين هذه المنتجات بسياج حديقة أو جدار أو سقف لثني المتسللين (حيوان أو بشري) عن اللمس أو التسلق.', N'يستثنى من ذلك منتجات مثل الحيوانات المندفعة / الردع (العشب / الحديقة).')
