INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007582, N'خدمات الكمبيوتر والتكنولوجيا', N'يشمل أي خدمات قد يتم وصفها / ملاحظتها على أنها خدمات متعلقة بتثبيت أجهزة وبرامج الكمبيوتر. يتضمن معالجة البيانات وخدمات قواعد البيانات.', N'')
