INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007593, N'خدمات التعبئة والتغليف / التخزين', N'يشمل أي خدمات يمكن وصفها / ملاحظتها على أنها خدمات لإعداد البضائع للنقل والتخزين والخدمات اللوجستية والبيع والاستخدام النهائي.', N'')
