INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007605, N'خدمات سفر', N' يشمل أي خدمات يمكن وصفها / ملاحظتها على أنها خدمات تشمل الإشراف والإشراف على الأعمال والسفر الشخصي. يشمل التخطيط والتنظيم.', N'يستثنى على وجه التحديد خدمات النقل.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10008275, N'ملحقات الدورات - أجهزة الكمبيوتر / معدات الملاحة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأجهزة كمبيوتر مستخدمة بشكل خاص في الدورات.', N'يستثني أجهزة الكمبيوتر / الملاحة لاستخدامها في المعدات الأخرى.')
