INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005378, N'أضواء السلامة الشخصية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها ضوء محمول ينبعث منه شعاع قوي ويمكن استخدامه لإرباك المهاجم أو كإشارة استغاثة تحدد موقع الفرد أو موقعه. يشمل المنتجات التي تتمتع بقدرة قوية وبعض الأضواء يمكن دمجها مع بوق هوائي.', N'باستثناء منتجات مثل مشاعل الأمان وأبواق الهواء التي لا تحتوي على مصابيح أمان مدمجة والمشاعل والمصابيح الكهربائية المصنفة في قطاع المستلزمات الكهربائية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006850, N'المركبات التي يتم التحكم فيها عن بعد', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مركبة موجهة عن بعد تستخدم للتصوير / التصوير لمناطق لا يمكن للطيار / السائق الوصول إليها. يمكن لهذه المنتجات اجتياز عدة أنواع من التضاريس أو أنواع محددة فقط.', N'تُستثنى من ذلك الألعاب الموجهة عن بُعد والمستخدمة فقط في أنشطة الاستجمام / الهوايات والمركبات التي لا يمكن قيادتها / التحكم فيها عن بُعد.')
