INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006851, N'قطع غيار واكسسوارات المركبات التي يتم التحكم فيها عن بعد', N'يشمل أي قطع غيار أو ملحقات للمنتجات التي يمكن وصفها / ملاحظتها على أنها مركبة يتم التحكم فيها عن بُعد.', N'باستثناء قطع غيار الألعاب التي يتم التحكم فيها عن بُعد.')
