INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005379, N'بخاخات الدفاع عن النفس', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها رذاذ فلفل مهيج أو رذاذ كريه الرائحة يمكن استخدامه كرادع ضد هجوم الإنسان والحيوان على حد سواء. تحتوي بعض البخاخات على صبغة فوق بنفسجية لتحديد هوية الشرطة. يشمل المنتجات التي يتم تنشيطها بمساعدة علبة غاز قابلة لإعادة التعبئة وكذلك بخاخات دفاعية مزودة بإنذار مدمج.', N'باستثناء منتجات مثل مسدسات الصعق.')
