INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007584, N'تشييد المباني والخدمات ذات الصلة', N'يشمل أي خدمات يمكن وصفها / ملاحظتها على أنها خدمات مقدمة من قبل مقاولين أو مقاولين من الباطن في تشييد المباني الدائمة أو صنعها. يشمل الخدمات المقدمة من قبل الأشخاص أو المنظمات العاملة في إعادة الأشياء إلى حالتها الأصلية.', N'')
