INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001873, N'بنادق الهواء', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مسدس أو بندقية ، والتي تستخدم عمودًا من الهواء المضغوط أو غازًا آخر لدفع قذيفة ، بما في ذلك جميع المنتجات (على سبيل المثال ، العسكرية ، وخدمة الطوارئ ، والمواصفات الصناعية أو المواصفات الخاصة الأخرى) التي تناسب المنتج تعريف.', N'يُستثنى من ذلك منتجات مثل Blowguns و Shotguns وأي مسدس غير موصوف على أنه Airgun.')
