INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001762, N'رمي المعدات الرياضية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها معدات رياضية أو معدات ميدانية مصممة خصيصًا للاستخدام في رياضات الرمي. تشمل منتجات مثل Javelins و Discus و Hammers.', N'يستثنى من ذلك منتجات مثل الكرات والفريسبي والبوميرانج.')
