INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001203, N'إكسسوارات تغليف الهدايا', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كعنصر مصمم لاستخدامه مع تغليف الهدايا لتعزيز أدائه ، وبعبارة أخرى للمساعدة في إغلاق الهدية وكذلك لتزيين الهدية. يتضمن منتجات مثل شريط تغليف الهدايا ، وريدات ، وأقواس ، وعلامات الهدايا ، وملصقات الهدايا.', N'باستثناء المنتجات مثل الأشرطة اللاصقة والخيوط والعلامات البريدية والملصقات.')
