INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001248, N'آلات مكتبية أخرى', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها منتجات Office Machinery ، حيث يتعذر على مستخدم المخطط تصنيف المنتجات في قوالب موجودة داخل المخطط.', N'يستثنى من ذلك جميع منتجات آلات المكاتب المصنفة حاليًا بما في ذلك آلات البريد / التعبئة والتغليف ومعدات العروض التقديمية وآلات التجليد والمقصلة وآلات التقطيع.')
