INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001259, N'حزم متنوعة القرطاسية التخطيط التنظيمي', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها منتجان أو أكثر من منتجات قرطاسية التخطيط التنظيمي المتميزة ، أي منتجين أو أكثر متضمنين في نفس الحزمة ، والتي تتقاطع داخل فئة قرطاسية التخطيط التنظيمي. يتضمن منتجات مثل المنظمات الشخصية التي تُباع مع إدخالات المنظم الشخصي. يجب إزالة العناصر المستلمة مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'يستثني منتجات مثل دفاتر العناوين التي تباع مع أجهزة الكمبيوتر المحمولة.')
