INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003534, N'مروج', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها شكل خشن من الملفات ذات أسنان حادة بارزة بشكل فردي ، وتستخدم لإزالة الخشب بسرعة.', N'باستثناء المنتجات مثل الملفات والطائرات والمواد الكاشطة.')
