INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005893, N'كتب التمرين', N'يتضمن أي منتجات يمكن وصفها / ملاحظتها كدفتر ملاحظات يتم استخدامه في المدارس لنسخ الأعمال المدرسية والملاحظات. يحتوي كتاب التمرين عادةً على ورق مُسطّر ولكن قد يكون الورق عاديًا والكتب متوفرة أيضًا بألوان وأحجام مختلفة', N'باستثناء منتجات مثل نماذج الأعمال المطبوعة مسبقًا ودفاتر الإيصالات ودفاتر المذكرات والأوراق الفردية الفردية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001313, N'تسميات / كوبونات / تذاكر', N'تتضمن أي منتجات يمكن وصفها / ملاحظتها على أنها قطع صغيرة من الورق مستطيلة الشكل في المعتاد ، والتي قد تكون مطبوعة مسبقًا أو عادية. يمكن أن يكون للنسخ المطبوعة ، التي لم يتم تخصيصها ، استخدامات متعددة مثل ملصقات الأمتعة ، والبطاقات ، والرموز ، والقسائم ، وتذاكر الدخول العامة ، وتذاكر السحب. الملصقات العادية غير المطبوعة غالبًا ما تكون ذاتية اللصق ويمكن استخدامها لتسمية الملفات والمستندات. تشمل منتجات مثل لفائف الملصقات والأوراق وكتب القسائم والشرائط وتذاكر الأوراق وبطاقات التمزق.', N'يستثني على وجه التحديد القسائم الترويجية التي تُستخدم لتسويق منتجات شركة معينة. باستثناء المنتجات مثل بطاقات الهدايا والملاحظات اللاصقة والمكعبات الورقية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001304, N'الورق / البطاقة - غير مطبوع', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أوراق أو بطاقات غير مطبوعة والتي قد تكون إما قطعًا منفصلة أو فضفاضة أو مجمعة معًا على هيئة وسادة أو بكرة أو كتاب. يتضمن منتجات مثل ورق التصوير وورق مطبوعات الكمبيوتر والبطاقات الملونة.', N'باستثناء المنتجات مثل الورق والبطاقات المطبوعة مسبقًا.')
