INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003578, N'عربات الأدوات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها حاوية بعجلات مع عدد من المقصورات التي يتم تخزين الأدوات فيها حتى يتمكن المستخدم من الوصول إليها بسهولة. السمة المميزة لعربة الأدوات هي وجود صينية أو سطح مستوٍ على أعلى مستوى يوفر سطحًا لوضع الأدوات. يشمل منتجات مثل عربات الأدوات مع تخزين الأدراج.', N'باستثناء المنتجات مثل الشاحنات اليدوية وخزائن الأدوات.')
