INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006780, N'مجموعة ذو طيات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة يدوية للإمساك بذراعين مفصليين و (عادة) فكوك مسننة ، معبأة ومباعة مع منتجات ذات طيات أخرى.', N'')
