INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10008308, N'كماشة / كماشة / قطع غيار القابض / الملحقات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها قطع غيار للزردية / الكماشة / المنتزعون.', N'باستثناء منتجات مثل منتجات كماشة / كماشة / غراببرز.')
