INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005723, N'أقمشة ورش العمل', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها قطعة قماش أو خرقة مصممة خصيصًا لاستخدام ورشة العمل. الأقمشة قوية وممتصة بما يكفي لتنظيف الزيت والطلاء اللاتكس وبعضها مغلف للسماح بتنظيف نشارة الخشب الناعمة من الخشب. تشمل مناشف الورش التي تستخدم لمرة واحدة وخرق لا تستخدم لمرة واحدة.', N'يستثنى من ذلك المنتجات مثل مناشف المطبخ الورقية.')
