INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003489, N'Nailsets / العدادات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها عمود صغير من المعدن ، بطول بضع بوصات ، مع نهاية واحدة مستديرة أو مربعة وواحدة مدببة إلى حد ما. عادة ما تكون النقطة المستدقة غير حادة ، ولكن في بعض الطرز تكون مقعرة أو مقعرة لحمل رؤوس الظفر. يشمل منتجات مثل الوصلات 1/32 بوصة.', N'يستثنى من ذلك منتجات مثل اللكمات المركزية والمخرز.')
