INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003461, N'قياس العجلات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة قياس تناظرية على شكل عجلة ، والتي يمكن تشغيلها على مسافات لقياسها بدقة وسرعة.', N'يستثني منتجات مثل شريط القياس.')
