INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003821, N'سلالم', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كمجموعة عمودية من الخطوات. تتكون السلالم من قطعتين جانبيتين متوازيتين متصلتين بسلسلة من الدرجات أو المتقاطع التي تستخدم للتسلق لأعلى ولأسفل للوصول إلى المناطق التي يتعذر الوصول إليها بأي طريقة أخرى. تشمل منتجات مثل سلالم التمديد وكذلك السلالم المتدرجة', N'يستثنى من ذلك منتجات مثل المقاعد المتدرجة والمنصات والسقالات.')
