INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007956, N'المصاعد والرافعات والرافعات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كمنتج يتم استخدامه لرفع أو دعم أو نقل ماكينات التحميل الثقيل أو الخزائن أو أي نوع آخر من الأحمال الثقيلة بأمان.', N'باستثناء منتجات مثل سلالم / مصاعد خدمة السيارات ، ورافعات المقطورة أو أي جهاز رفع آخر يستخدم في قطاع السيارات. لا يشمل على وجه التحديد ساحبات البكرات ، والبكرات / محركات الحزام ، ورافعات الشفط.')
