INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006778, N'مجتذب البكرة (اليد)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة غير تعمل بالطاقة تُستخدم لتركيب أو إزالة بكرات ميكانيكية مختلفة وأجزاء ميكانيكية مضغوطة أو مضغوطة مماثلة. على الرغم من ارتباطها الأكثر شيوعًا بإزالة وتركيب بكرات السيارات والمحامل والتروس ، إلا أن أدوات سحب البكرة تستخدم في مجموعة متنوعة من التطبيقات الصناعية والاستهلاكية. تشمل أنواع ساحبات البكرات ساحبات الفك اليدوية ، والسحابات التوافقية ، والسحابات الهيدروليكية ، والسحابات الهوائية ، والعديد من تصميمات ساحب البكرة لآلات معينة وتطبيقات أخرى.', N'باستثناء ساحبات البكرات التي تعمل بالطاقة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005679, N'البكرات ومحركات الحزام', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز لرفع وخفض الأوزان ، وتتكون من عجلة ذات حافة محززة يمر فوقها حبل أو حزام.', N'باستثناء منتجات مثل Dolleys و Rollers و Wheelbarrers و Suction Lifters والبكرات المصممة خصيصًا للرحلات وتسلق الجبال.')
