INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003504, N'المطرقة تاكر - غير تعمل بالطاقة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها آلة محمولة باليد تستخدم لتثبيت غلاف المنزل ، والعزل ، وشعر الأسقف في الموضع.', N'باستثناء المنتجات مثل الدباسات.')
