INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006783, N'مشاعل الغاز', N'شعلة الغاز هي جهاز لتوليد لهب لتسخين المنتجات باستخدام وقود غازي مثل الأسيتيلين أو الغاز الطبيعي أو البروبان. تحتوي بعض المشاعل على مدخل هواء لخلط غاز الوقود مع الهواء لإحداث احتراق كامل.', N'باستثناء مشاعل اللحام.')
