INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006258, N'بنادق السد (غير تعمل بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة لطرد السد من الأنبوب. يمكّن مسدس السد "حبة" من المادة ليتم تطبيقها على الشقوق والدرزات. يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة يدوية مصممة خصيصًا لمعالجة المفاصل في البناء أو الإطارات الزجاجية عن طريق ملئها بالملاط لتحسين المظهر أو الحماية من العوامل الجوية.', N'يستثنى من ذلك الأدوات الكهربائية وجميع الأدوات المدببة الأخرى.')
