INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10008279, N'بنادق العزل (غير بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة غير تعمل بالطاقة مصممة خصيصًا لعزل التطبيقات عن طريق سد الفجوات والشقوق. هذه المنتجات متصلة مباشرة بحاوية الضغط. يشمل منتجات مثل مسدسات الرغوة.', N'يستثنى من ذلك منتجات مثل مسدسات الغراء ومسدسات الهواء ومسدسات السد ومسدسات السوائل.')
