INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005716, N'مقاييس شطبة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كمقياس قابل للتعديل لضبط الزوايا ونقلها. عادة ما يكون المقبض مصنوعًا من الخشب أو البلاستيك ويتم توصيله بشفرة معدنية بمسمار إبهامي أو صامولة جناح. تدور الشفرة ويمكن قفلها بأي زاوية عن طريق فك أو شد المسمار اللولبي.', N'يستثنى من ذلك المنتجات مثل البوصلات والفرجار والمنقلة.')
