INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006779, N'ساحبة البكرة (بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة تعمل بالطاقة تُستخدم لتركيب أو إزالة بكرات ميكانيكية متنوعة وأجزاء ميكانيكية مضغوطة أو مضغوطة مماثلة. على الرغم من ارتباطها الأكثر شيوعًا بإزالة وتركيب بكرات السيارات والمحامل والتروس ، إلا أن أدوات سحب البكرة تستخدم في مجموعة متنوعة من التطبيقات الصناعية والاستهلاكية. تشمل أنواع ساحبات البكرات ساحبات الفك اليدوية ، والسحابات التوافقية ، والسحابات الهيدروليكية ، والسحابات الهوائية ، والعديد من تصميمات ساحبة البكرة لآلات معينة وتطبيقات أخرى.', N'يستثنى ساحبات البكرة غير المزودة بالطاقة.')
