INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003729, N'قواطع سطحية - ثابتة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة طاقة ثابتة مصممة لجعل وجه واحد وجانب واحد من اللوحة مسطحة ، بزاوية 90 درجة بينهما. يشمل المنتجات التي تؤدي مهام أخرى ، مثل الضرب بالأربطة والتناقص.', N'يستثنى من ذلك الأنواع الأخرى من المفاصل والمقاسات.')
