INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003596, N'مناشير مبللة / بلاط / قواطع زجاجية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة كهربائية ثابتة تستخدم شفرة ماسية لقطع البلاط باستخدام الماء لتبريد الشفرة. تشمل المنتجات ذات الشفرات التي تتراوح من الأنواع إلى قطع السيراميك والرخام إلى الجرانيت والبورسلين.', N'باستثناء المناشير المحمولة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007026, N'المسدسات الكاشطة / المنظفات الرملية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة كهربائية لدفع تيار من المواد الكاشطة (مثل الرمل) بالقوة ضد سطح تحت ضغط عالٍ لتنعيم سطح خشن أو تخشين سطح أملس أو تشكيل سطح أو إزالة ملوثات السطح .', N'باستثناء بكرات الصنفرة وأجهزة الصنفرة النهائية وأجهزة الصنفرة التفصيلية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003555, N'ضواغط هواء - محمولة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها ضاغط يأخذ الهواء عند الضغط الجوي ويوصله عند ضغط أعلى. يمكن تركيب ضواغط الهواء المحمولة الصغيرة والخفيفة نسبيًا على عجلات أو نقلها إلى مواقع العمل بسهولة.', N'يستثنى من ذلك منتجات مثل البخاخات وكذلك ضواغط الهواء الثابتة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003644, N'زاوية المطاحن', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة تعمل بالطاقة لتنعيم طبقات اللحام وإزالة الصدأ وإزالة الطلاء. طاحونة الزاوية هي أداة محمولة تعمل بمحرك كهربائي أو محرك بنزين. جلاخة الزاوية لها محامل كبيرة لمواجهة القوى الجانبية الناتجة أثناء القطع. غالبًا ما تستخدم المطاحن الزاوية عددًا لا يحصى من الأقراص المختلفة لمواد ومهام مختلفة مثل القطع والتلميع والنشر.', N'يستثنى من ذلك منتجات مثل المناشير الدائرية والمثاقب وكذلك جميع أجهزة الصنفرة التي تعمل بالطاقة اليدوية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003619, N'أجهزة قياس الزاوية (بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة رقمية مصممة خصيصًا لقياس الزوايا. مقياس الزاوية هو مقياس إلكتروني ، عادةً بقدمين متصلتين بمحور قابل للضبط ، يوفر قراءة رقمية LED تعبر بالدرجات عن العلاقة بين خطين أو مستويين يتباعدان عن نقطة واحدة.', N'باستثناء منتجات مثل قياسات الزاوية اليدوية أو اليدوية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003651, N'لحام القوس', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة كهربائية مستخدمة هي عملية الانضمام أو قطع جزأين معدنيين. يستخدم الجهاز أقطابًا كهربائية عالية الجهد لتوليد حرارة مكثفة ومركزة ، والتي تستخدم بدورها في لحام وقطع المعدن.', N'يستثنى من ذلك منتجات مثل مشاعل النفخ ومكاوي اللحام.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007031, N'خلاط / هزاز', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة كهربائية لخلط المواد مثل الخرسانة أو الجبس.', N'باستثناء أدوات المطبخ والألعاب وملحقات العلاقة الحميمة الشخصية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003660, N'المطارق الدوارة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أداة تعمل بالهواء المضغوط تنتج قوة تأثير هائلة للحفر في الخرسانة والنقر بالإزميل في أعمال البناء.', N'يستبعد جميع التدريبات الأخرى.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003737, N'الموجهات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أداة تعمل بالطاقة تعمل على طحن سطح المعدن أو الخشب. يشمل الأدوات التي لها قاعدة قابلة للإزالة.', N'يستثني أجهزة توجيه الكمبيوتر.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005231, N'مناشير الفرقة - محمولة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة كهربائية محمولة مصممة بحزام فولاذي مستمر يحركه محرك يحركه حول بكرتين أو ثلاث بكرات. يتم دفع العمل إلى الشفرة عبر طاولة متكاملة. يمكن حمل المناشير الشريطية المحمولة الصغيرة نسبيًا وخفيفة الوزن إلى موقع العمل. يشمل منتجات مثل المناشير الشريطية الكهربائية السلكية.', N'باستثناء المنتجات مثل المناشير الشريطية الثابتة وجميع المناشير الكهربائية المحمولة الأخرى.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003742, N'آلات صنفرة الحزام - محمولة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة كهربائية محمولة مصممة لاستخدام حزام من الورق الكاشطة المشدود على بكرة تعمل بمحرك لتحسين الاتصال مع المادة. تُستخدم المنتجات لصنفرة مساحات كبيرة من المخزون بسرعة ، مثل أسطح الطاولات والأرضيات والأبواب.', N'باستثناء آلات الصنفرة التفصيلية وآلات السنفرة الثابتة بالحزام.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005718, N'نجارة البسكويت', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة نجارة تستخدم لربط قطعتين من الخشب معًا. تعمل شفرة المنشار الدائرية المائلة من كربيد التنجستن على قطع ثقب على شكل هلال (يسمى الفم) في الحواف المتقابلة لقطعتين من الخشب وبسكويت خشبي بيضاوي الشكل ومضغوط للغاية ومُغطى بالغراء ويوضع في الفتحة. ثم يتم تثبيت اللوحين معًا وربطهما.', N'يستثنى من ذلك المنتجات مثل المفاصل والمقاسات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005214, N'آلات خلط الأسمنت / الملاط', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها آلة تعمل بالطاقة ، لها أسطوانة دوارة يتم فيها دمج الأسمنت والرمل والحصى والماء في الخرسانة. يتم تشغيل هذه المنتجات بالكهرباء أو البنزين وهي محمولة للاستخدام في موقع العمل المطلوب.', N'يستثنى من ذلك جميع معدات الرفع والمناولة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005223, N'مناشير دائرية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أداة قطع محمولة شديدة التحمل ومثالية لقطع وتنظيف وشق جميع أنواع المعادن والبناء. المنشار الدائري هو منشار كهربائي يحتوي على قرص فولاذي مع أسنان تقطيع على الأطراف ويدور على مغزل. يتم استخدام المنشار الدائري للقطع الخام. يشمل مناشير دائرية بوظيفة الفرملة الكهربائية.', N'يُستثنى من ذلك مناشير الطاولة التي تعمل بالطاقة ، والمناشير المترية ، والمناشير الدوارة ، والمناشير الترددية ، فضلاً عن مناشير التركيب.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003645, N'أدوات القطع', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة محمولة تعمل بالطاقة لقطع الأنابيب وأنابيب الصلب والـ PVC. تحتوي هذه المنتجات على محرك وعجلة جلخ ، والتي تقطع معًا المواد المطلوبة.', N'يستثنى من ذلك أدوات ومناشير القطع اليدوية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007028, N'هدم مطرقة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة تعمل بالطاقة تجمع بين المطرقة والإزميل ، والمعروف أيضًا باسم المثقاب الهوائي أو آلة ثقب الصخور. يشمل الاستخدام كسر الرصيف أو الخرسانة أو الصخور.', N'يستثنى من ذلك مثاقب المطرقة / المطارق الدوارة ومجموعة أدوات الحفر ذات المطرقة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003672, N'التفاصيل ساندرز', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها آلة صنفرة كهربائية بقاعدة على شكل مكواة ملابس. يستخدم الطرف المدبب لصنفرة الزوايا والمساحات الضيقة. تلتصق قاعدة ورق الصنفرة بالماكينة بشريط خطاف وحلقة وتستخدم للتمليس الفعال للخشب المحيط. يتضمن صنفرة التفاصيل مع وسادات تلميع أو تجريد.', N'باستثناء الأنواع الأخرى من أجهزة الصنفرة المتخصصة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005248, N'صنفرة قرصية / صنفرة دريوال - محمولة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة كهربائية محمولة مع قرص دوار لتركيب مادة كاشطة لتشكيل الأسطح وتنعيمها. هذه المنتجات خفيفة وصغيرة نسبيًا ويمكن نقلها إلى موقع العمل.', N'يستثنى من ذلك أجهزة الصنفرة بالحزام المحمولة وكذلك أجهزة الصنفرة ذات الأقراص الثابتة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003618, N'أجهزة قياس المسافة / الخطية (بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة تعمل بالطاقة مصممة خصيصًا لقياس المسافة الخطية و / أو طول كائن من موضع ثابت. غالبًا ما تقدم هذه المنتجات وظائف إضافية مثل قياسات المنطقة.', N'يستثني منتجات مثل المقاييس الخطية غير المزودة بالطاقة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003741, N'مثقاب استنزاف (بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة كهربائية محمولة تستخدم لإزالة السدادات في الأنابيب من خلال محاولة تفكيك الانسداد ، بدلاً من نقله ببساطة في الأنبوب. تشمل الأدوات الكهربائية المعروفة أيضًا باسم الثعابين.', N'يستثنى من ذلك مثاقب التصريف اليدوية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003653, N'المثقاب / السائقين (بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة تعمل بالطاقة بنقطة حادة وحواف قطع لعمل ثقوب في المواد الصلبة عن طريق الدوران السريع.', N'يستثنى من ذلك منتجات مثل المثاقب التي لا تعمل بالطاقة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003658, N'التدريبات - مختلطة (تعمل بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة كهربائية ذات طرفين تجمع بين نقطة حفر وغطاء عكسي. يتم استخدامه لإنتاج ثقوب مركزية محمية.', N'يستبعد جميع التدريبات الأخرى.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003669, N'تشطيب ساندرز', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها آلة تعمل بالطاقة تستخدم للصنفرة النهائية في مشاريع الأعمال الخشبية. (مثل Orbital Sander.) يشمل المنتجات التي تقبل إما ورق الصنفرة العادي أو الألواح المدعومة ذاتية اللصق.', N'باستثناء آلات الصنفرة اليدوية وآلات السنفرة الكنتورية والتفصيلية والحزام.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003638, N'قواطع الرغوة - تعمل بالطاقة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة تعمل بالطاقة تعمل عن طريق تسخين سلك خاص إلى النقطة التي يمكن أن يبخر فيها الرغوة التي تلامس معها.', N'باستثناء قواطع الرغوة اليدوية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005211, N'مولدات كهرباء', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها آلة تحول الطاقة الميكانيكية إلى كهرباء. يتكون من مغناطيس يدور في سلك ملف ، والذي يحرر الإلكترونات ويدفعها على طول السلك.', N'يستثني أي نوع آخر من المولدات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005213, N'بنادق الغراء - تعمل بالطاقة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة كهربائية مصممة لتطبيق مادة لاصقة ساخنة على السطح عن طريق إذابة أعواد الغراء الصلبة القابلة للاستهلاك التي يتم إدخالها فيها.', N'يستثنى من ذلك مسدسات السد والمسدسات الحرارية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003663, N'بنادق الشحوم (بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة مستخدمة لوضع المزلقات من خلال فتحة العدسة على نقطة معينة ، عادة على حلمة الشحوم. تؤدي القنوات الموجودة خلف حلمة الشحوم إلى المكان المطلوب للتزييت. قد تكون الفتحة من النوع الذي يتناسب بشكل وثيق مع فتحة استقبال على أي عدد من الأجهزة الميكانيكية. يضمن التركيب المحكم للفتحات تطبيق مواد التشحيم عند الحاجة فقط.', N'باستثناء المسدسات الحرارية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007027, N'طقم مثقاب المطرقة وتأثير السائق', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كمجموعة من الأدوات التي تعمل بالطاقة والتي تُستخدم لحفر الثقوب والمطرقة والقطع في الخرسانة.', N'يُستثنى من ذلك مطارق التكسير ، والمثاقب المطرقية / المطارق الدوارة ، ومحركات التصادم التي تُباع بشكل فردي.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003659, N'تدريبات المطرقة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة كهربائية تشبه المثقاب الكهربائي القياسي ، باستثناء أنها مزودة بمطرقة لحفر البناء. قد يتم تعشيق عمل المطرقة أو فك ارتباطه حسب الحاجة.', N'يستبعد جميع التدريبات الأخرى.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003662, N'البنادق الحرارية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة تعمل بالطاقة للمساعدة في تجريد الطلاء. تبدو المسدسات الحرارية مثل مجففات الشعر إلى حد كبير ولكن لا ينبغي استخدامها لتجفيف الشعر! تشبه طريقة تشغيل مسدس الهواء الساخن مجفف الشعر الذي يسحب الهواء إلى جسم الأداة ويدفعه عبر عنصر تسخين كهربائي ويخرج من خلال فوهة. من أجل تجريد الطلاء ، يتم توجيه الهواء الساخن إلى السطح المطلي ، مما يؤدي إلى تليينه بحيث يمكن تجريده بسهولة.', N'يستثنى من ذلك مسدسات الشحوم ، ومجففات الشعر ، والسد ، ومسدسات الصمغ.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003655, N'محركات التأثير', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة كهربائية ، والتي يتم تصنيعها ليس فقط لتدوير المثقاب أو بت المحرك ، ولكن لتطبيق سلسلة من ضربات المطرقة الدوارة عالية السرعة للسائق. تقلل هذه القوة الدورانية من القوة المحورية المطلوبة لإبقاء لقمة المحرك في التجويف ، لذلك يبدو أن البراغي "تذوب" فقط.', N'باستثناء التدريبات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003656, N'مفاتيح الربط', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة كهربائية للإمساك بالأشياء أو لفها أو لفها مثل الصواميل أو البراغي أو الأنابيب. يشمل المنتجات ذات الفكوك الثابتة أو القابلة للتعديل.', N'يستثني العوامل المؤثرة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007976, N'مرشحات مكنسة كهربائية صناعية رطبة / جافة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كمرشح يستخدم في أي نوع من المكانس الكهربائية الصناعية ذات البناء الرطب / الجاف. يتضمن مرشحات خاصة بالعلامة التجارية وفلاتر عالمية.', N'يستثنى من ذلك منتجات مثل فلاتر المكنسة الكهربائية أو فلاتر المكنسة الكهربائية المنزلية أو أي نوع آخر من قطع الغيار / الملحقات الخاصة بالمكانس الكهربائية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007978, N'رؤوس مكنسة كهربائية صناعية رطبة / جافة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها فرشاة رأس أو أداة شق أو أي رأس آخر يستخدم مع أي نوع من المكنسة الكهربائية الصناعية الرطبة / الجافة.', N'يستثنى من ذلك منتجات مثل رؤوس المكنسة الكهربائية أو رؤوس المكنسة الكهربائية المنزلية أو أي نوع آخر من قطع الغيار أو ملحقات المكانس الكهربائية. يستثنى على وجه التحديد خراطيم / أنابيب المكنسة الكهربائية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007977, N'خراطيم / أنابيب مكنسة كهربائية صناعية رطبة / جافة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أنبوب أو خرطوم مستخدم مع أي نوع من المكنسة الكهربائية الصناعية الرطبة / الجافة.', N'يستثنى من ذلك المنتجات مثل الخراطيم / الأنابيب المفرغة من الأنابيب أو خراطيم / أنابيب المكنسة الكهربائية المنزلية أو أي نوع آخر من قطع الغيار أو ملحقات المكانس الكهربائية. يستثنى على وجه التحديد رؤوس المكانس الكهربائية أو أدوات الشقوق.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007975, N'المكانس الكهربائية الصناعية الرطبة / الجافة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أجهزة كهربائية متحركة مصممة لامتصاص الغبار والأوساخ والحطام والسائل. تستخدم في الغالب في الأماكن ذات الأحجام الكبيرة أو الجزيئات الكبيرة من الأوساخ ، مثل مواقع البناء.', N'لا يشمل المكانس الكهربائية المنزلية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003631, N'بانوراما - تعمل بالطاقة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها منشار كهربائي ذو أسنان دقيقة بشفرة ضيقة تستخدم لقطع الخطوط العريضة المنحنية أو المعقدة أو غير المنتظمة في مادة ما.', N'باستثناء مناشير التركيب اليدوية وكذلك جميع المناشير الأخرى المصنفة حاليًا والتي تعمل بالطاقة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003616, N'قواطع موصل - محمولة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة كهربائية محمولة تسوي حافة أو سطح واحد من عنصر خشبي في كل مرة. محرك في الأداة يقود سكينًا دوارًا مصممًا بشكل أساسي لتقويم وتسطيح حواف المادة. يشمل المنتجات التي يمكن حملها أو نقلها بسهولة.', N'يُستثنى من ذلك المنتجات مثل الطائرات التي لا تعمل بالطاقة وكذلك أجهزة قياس الوصلة الثابتة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003626, N'قادين صفح', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة محمولة تعمل بالطاقة للتشذيب العام للأرضيات الخشبية وغيرها من المنتجات المصفحة. يتضمن أدوات لقص الخط الخلفي والزوايا والزوايا وإنشاء طبقات متطابقة.', N'يستثنى من ذلك أدوات تشذيب الصفائح غير المزودة بالطاقة وموجهات الأخشاب.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003620, N'مستويات الليزر', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة تنبعث منها شعاع ليزر ، مما يولد خطًا مستقيمًا مرئيًا على السطح الذي توضع عليه الأداة ويتخطى العيوب الصغيرة والقوالب والمسافات غير المستوية. تجمع الأداة بين مستوى روح و / أو بندول مع ليزر للإشارة إلى خط مستوي على سطح. يشمل الأدوات المصممة للاستخدام في الداخل والخارج.', N'يستثني جميع منتجات التسوية التي لا تعمل بالطاقة والتي لا تشتمل على تقنية الليزر.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006277, N'أجهزة الكشف عن المعادن', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز محمول باليد يستخدم لتحديد موقع المعادن ، وخاصة المعادن الثمينة.', N'يُستثنى من ذلك مكتشفات الحافة ومكتشفات المركز ومسدسات المسامير ومكتشفات مسمار ومسدسات اللولب.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003738, N'مناشير ميتري - محمولة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة كهربائية محمولة لعمل قطع ميتري مركب. تتيح هذه الأداة للمستخدم قطع الخشب بزوايا دقيقة عن طريق ضبط الرقصة المعدنية على الزاوية المطلوبة. يتم استخدامه عندما تكون الزاوية المثالية أمرًا حتميًا ، مثل قضبان الحشو أو الحواف أو قضبان دادو.', N'يستثني جميع أنواع المناشير الأخرى.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003666, N'مسدسات الأظافر (بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها آلة تعمل بالطاقة لقيادة المسامير في المواد. عادة ما تكون هذه المنتجات مدفوعة بالكهرومغناطيسية أو الهواء المضغوط أو عبوة ناسفة صغيرة. يشمل منتجات مثل مسدسات المسامير التي تعمل بالغاز المضغوط.', N'يستثنى من ذلك المنتجات مثل البنادق الأساسية والمسدسات الحرارية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003632, N'القضم / المقصات - معدنية (كهربائية)', N'يشمل أي منتجات قد يتم وصفها / ملاحظتها على أنها أداة تعمل بالطاقة تُستخدم لقص المعدن من خلال رفع شفرة القطع مقابل قالب ثابت مع وضع الصفائح المعدنية بينهما.', N'يستثنى من ذلك أدوات القطع الكهربائية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007032, N'تتأرجح Multitools', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة توفر وظائف متعددة مثل القطع ، النشر ، الصنفرة ، الكشط ، التلميع ، التشكيل ، إزالة الجص.', N'يستثنى من ذلك الأدوات المتعددة الدوارة والملحقات / قطع الغيار للأدوات المتعددة المتذبذبة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005653, N'أدوات تطبيق الطلاء - تعمل بالطاقة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة تعمل بالطاقة مصممة خصيصًا للتطبيق الخاضع للرقابة للطلاء ، داخل بيئة منزلية. تتوفر هذه المنتجات في تصميمات مختلفة وقادرة على حمل كميات متفاوتة من الطلاء. يشمل منتجات مثل رشاشات الطلاء التي تعمل بالطاقة.', N'يستثني منتجات مثل أدوات تطبيق الطلاء العامة غير المزودة بالطاقة بالإضافة إلى جميع أدوات تطبيق الطلاء الخاصة بالفنانين.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003640, N'قواطع الأنابيب (بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة تعمل بالطاقة لقطع أطوال الأنابيب بدقة. يشمل المنتجات التي تقطع بإزاحة مادة الأنابيب بدلاً من إزالتها.', N'باستثناء قواطع الأنابيب اليدوية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003750, N'أدوات كهربائية - محمول باليد أخرى', N'يتضمن أي منتجات يمكن وصفها / ملاحظتها على أنها أدوات طاقة - عناصر محمولة ، حيث يتعذر على مستخدم المخطط تصنيف المنتجات الموجودة في وحدات البناء الموجودة داخل المخطط.', N'يستثني جميع الأدوات الكهربائية المحمولة المصنفة حاليًا.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003747, N'أدوات كهربائية - قطع غيار / ملحقات محمولة باليد', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها قطع غيار أو ملحقات للأدوات الكهربائية المحمولة باليد. يشمل منتجات مثل قطع الحفر الإضافية وشفرات استبدال المناشير.', N'يستثنى من ذلك منتجات مثل الأدوات الكهربائية المحمولة المحمولة باليد.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003629, N'مناشير ترددية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أداة كهربائية محمولة بشفرة مستقيمة مركبة في نهاية جسم يشبه البندقية ، حيث تتحرك الشفرة للأمام والخلف أثناء القطع. تحتوي هذه المنتجات عمومًا على صفيحة تركب على السطح المقطوع. يشمل المنتجات التي يمكن استخدامها مع مجموعة متنوعة من الشفرات حسب التطبيق والقطع المطلوب.', N'يستثنى من ذلك المناشير الجنزيرية ومناشير الطاولة وكذلك المناشير غير المزودة بالطاقة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007033, N'أدوات دوارة متعددة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة دوارة يمكن استخدامها للحفر أو الصنفرة.', N'يستثنى من ذلك المثقاب / المشغلات والمفكات / الأدوات الدوارة والمطارق الدوارة والأدوات المتعددة المتذبذبة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003641, N'مناشير دوارة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة تعمل بالطاقة لقطع الأخشاب والمواد الأخرى عن طريق تدوير الظرف في الاشتباك مع منشار دوار (مستوي أو مقعّر).', N'يستثني جميع أنواع المناشير الأخرى.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007034, N'بكرات سنفرة (تعمل بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة تعمل بالطاقة للصنفرة عن طريق أتمتة عملية فرك ورق الصنفرة على السطح.', N'باستثناء المسدسات الكاشطة / صنفرة الرمل ، وأجهزة الصنفرة النهائية ، وأجهزة الصنفرة التفصيلية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003657, N'البنادق اللولبية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة كهربائية لاستخدام البراغي في مجموعة واسعة من التطبيقات ، من تعليق الحوائط الجافة إلى تثبيت سطح السفينة إلى تثبيت الزخرفة. تشمل المنتجات والمنتجات اللاسلكية التي يتم تحميلها آليًا ويدويًا.', N'باستثناء المفكات الكهربائية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003654, N'مفكات (تعمل بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة كهربائية لقيادة البراغي ، والتي لها طرف يلائم رأس المسمار. هذه المنتجات لها نصائح قابلة للتبديل.', N'يستثنى من ذلك منتجات مثل المفكات متعددة الأطراف والأدوات الدوارة المتعددة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003649, N'لحام / لحام الحديد', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة كهربائية بطرف قابل للتسخين يستخدم لصهر وتطبيق اللحام.', N'يستثنى من ذلك منتجات مثل مشاعل اللحام ومشاعل اللحام.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003665, N'دباسات (تعمل بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها آلة تعمل بالطاقة لقيادة المواد الغذائية إلى المنزل.', N'باستثناء المنتجات مثل دباسات القرطاسية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003647, N'المطاحن المستقيمة / القوالب', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة محمولة تعمل بالطاقة تعالج المواد عن طريق الطحن أو التكسير ، مع مغزل ممتد للاستخدام في المواقف الضيقة. يشمل المطاحن المستقيمة والقوالب.', N'باستثناء الأدوات اليدوية والمطاحن السطحية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005869, N'مكتشفات / كاشفات / مجسات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز محمول باليد يستخدم لتحديد موقع المسامير الخشبية والمعدنية المستخدمة في بناء الإطار الخفيف بعد تثبيت سطح الجدار. يتضمن مكتشفات الحافة ومكتشفات الوسط.', N'باستثناء مسدسات المسامير والمسامير اللولبية وأجهزة الكشف عن المعادن.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003648, N'المطاحن السطحية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة محمولة تعمل بالطاقة للآلة التي تعالج المواد عن طريق الطحن أو التكسير.', N'باستثناء الأدوات اليدوية والمطاحن المستقيمة أو القوالب.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003615, N'مقاشط السطح / السماكة - محمولة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة كهربائية تعمل على تنعيم سطح الخشب. محرك في المسوي السطحي يقود آلية تلقيم تلقائية وسكين دوار يعمل على تسطيح وتقليم سمك الخشب على وجه التحديد إلى حجم محدد مسبقًا. يشمل المنتجات التي يمكن حملها أو نقلها بسهولة.', N'باستثناء المنتجات مثل الطائرات التي لا تعمل بالطاقة والطائرات الثابتة التي تعمل بالطاقة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003627, N'مناشير الطاولة - محمولة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها منشار دائري محمول يعمل بالطاقة ويستخدم لمجموعة من أعمال القطع بواسطة عمال الخشب في المنزل. تشمل المناشير المحمولة التي تعمل بالكهرباء والمثبتة على المنضدة.', N'يُستثنى من ذلك المناشير ذات السلسلة والمنشار التركيبي وكذلك الأنواع الأخرى من المناشير الدائرية التي تعمل بالطاقة.')
