INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003884, N'أدوات تغطية الجدران / السقف - تعمل بالطاقة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أداة تعمل بالطاقة تُستخدم خصيصًا لمساعدة المستخدم في تطبيق أو إزالة غطاء معين من / إلى الجدران أو الأسقف. يتضمن منتجات مثل أدوات تقشير ورق الحائط ، وأدوات كهربائية للسقف ، وأجهزة بخار للجدران.', N'يستثنى من ذلك منتجات مثل أدوات تغطية الجدران / السقف / الأرفف التي لا تعمل بالكهرباء.')
