INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005162, N'ألعاب الدفع / السحب (تعمل بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها لعبة حيوانات أو مركبة تعمل بالطاقة والتي يمكن دفعها أو سحبها باستخدام مقبض أو حبل. تعمل طاقة البطارية على تمكين الأضواء والأصوات الساطعة لتسلية طفل صغير.', N'يستثني ألعاب الدفع أو السحب التي لا تعمل بالطاقة وألعاب تحفيز الأطفال')
