INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005194, N'لعبة المركبات - غير قابلة للركوب (تعمل بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها لعبة سيارة تعمل بالطاقة ولا يجوز للطفل أن يركبها. وهذا يشمل أي نوع من الألعاب الفردية مثل طائرات الهليكوبتر والقوارب والطائرات والسيارات والشاحنات والطائرات بدون طيار وما إلى ذلك. قد تكون أيضًا ملحقًا لدمية أزياء أو دمية أكشن. تتضمن سيارة لعبة يتم التحكم فيها عن بُعد باستثناء الأطقم والأطقم.', N'يستثنى من ذلك منتجات مثل المركبات غير المزودة بمحركات ومركبات الركوب والمركبات التي لا تحتوي على ألعاب والتي يتم التحكم فيها عن بُعد (مثل الأمن).')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005195, N'لعبة المركبات - أخرى غير قابلة للركوب', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها سيارة ألعاب بدون ركوب ، حيث لا يتمكن مستخدم المخطط من تصنيف المنتجات في الطوب الموجود داخل المخطط.', N'يستثنى من ذلك جميع السيارات المصنفة حاليًا والتي لا تستخدم ألعاب الركوب.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005196, N'لعبة المركبات - حزم متنوعة غير قابلة للركوب', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها اثنتان أو أكثر من سيارات الألعاب المميزة غير القابلة للركوب التي يتم بيعها معًا ، والتي توجد داخل المخطط والتي تنتمي إلى قوالب مختلفة ولكن تنتمي إلى نفس الفئة ، أي منتجين أو أكثر متضمنين في نفس العبوة والتي تتقاطع طوب ضمن فئة سيارات اللعب (غير الركوب). تتضمن منتجات مثل مجموعة قطار ومسارات بديلة تُباع معًا. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'باستثناء منتجات مثل مجموعة القطار الكاملة ودمية الحركة ومجموعة متنوعة من سيارات الدمى.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005187, N'الألعاب - الركوب (بدون آلية)', N'تتضمن أي منتجات يمكن وصفها / ملاحظتها على أنها لعبة غير مزودة بالطاقة مصممة خصيصًا ليتم ركوبها من قبل المستخدم. تشمل منتجات مثل الدراجات البخارية والخيول الهزازة.', N'باستثناء منتجات مثل ألعاب الركوب التي تعمل بالطاقة ومركبات الألعاب والدراجات بدون الركوب ، حيث يتم تصنيف الأخيرة في قسم المعدات الرياضية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005188, N'الألعاب - الركوب (آلي)', N'تشمل أي منتجات يمكن وصفها / ملاحظتها على أنها لعبة تعمل بالطاقة ومصممة خصيصًا لركوبها من قبل المستخدم. تشمل منتجات مثل سيارة تعمل بالبطارية أو دراجة نارية.', N'باستثناء منتجات مثل ألعاب الركوب غير المزودة بالطاقة ومركبات الألعاب والدراجات التي لا تعمل بالطاقة ، والتي يتم تصنيفها في فئة المعدات الرياضية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005441, N'الألعاب - إكسسوارات الركوب', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها ملحقات للعبة الركوب. يتضمن منتجات مثل لعبة أجراس الدراجات ومقود اللافتات.', N'باستثناء منتجات مثل قطع غيار الدورات الرياضية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10008049, N'الطائرات', N'تشمل أي مركبات يمكن وصفها / ملاحظتها على أنها طائرة. تشمل منتجات مثل الطائرات والمروحيات والحزم النفاثة.', N'باستثناء المنتجات مثل الطائرات بدون طيار والمظلات وجميع أنواع المركبات الأخرى.')
