INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003757, N'كاشطات الجليد (السيارات)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز ، مصممة خصيصًا لكشط الجليد من نوافذ السيارة.', N'يستثنى من ذلك أجهزة غسيل النوافذ.')
