INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005233, N'ملحقات البطارية (السيارات)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أحد الملحقات المستخدمة مع بطارية السيارة. يشمل منتجات مثل كابلات البطاريات.', N'باستثناء بطاريات السيارات الكاملة.')
