INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005443, N'النكات العملية', N'يتضمن أي منتجات يمكن وصفها / ملاحظتها كعنصر يؤدي إلى حدوث حدث غير متوقع يهدف إلى إنشاء التسلية والمرح. يشمل منتجات مثل الوسائد الصاخبة ومسحوق الحكة.', N'باستثناء المنتجات مثل الألغاز.')
