INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006899, N'لعب / ألعاب - أخرى', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها لعبة / لعبة ، حيث لا يستطيع مستخدم المخطط تصنيف المنتجات الموجودة في قوالب موجودة داخل المخطط.', N'باستثناء جميع الألعاب / الألعاب المصنفة حاليًا.')
