INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005129, N'قطع غيار / ملحقات منتجات مكافحة السرقة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها قطع غيار أو ملحقات لمنتجات الحماية من السرقة. تشمل منتجات مثل صواميل قفل العجلات ومفاتيح القتل.', N'باستثناء أجهزة التعقب وجميع المنتجات التي يتم تقديمها حاليًا في فئة منتجات مكافحة السرقة.')
